mapper.select:
SELECT * 
FROM STORE
WHERE frRegiNum = #{frRegiNum}
AND frPass = #{frPass}

SELECT * 
FROM emp
WHERE empnum = #{empnum}
AND pass = #{pass}

mapper.update:
UPDATE EMPCHECKIN
	SET OFFTIME = SYSDATE
WHERE clerkNum = #{clerkNum}
AND TO_CHAR(ONTIME,'YYYY-MM-DD') = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
AND FRREGINUM =#{FrRegiNum}
